import React from "react";
function normalizeDate(str) {
  if (!str) return "";
  if (/^\d{2}[.\-/]\d{2}[.\-/]\d{4}$/.test(str)) {
    const [dd, mm, yyyy] = str.split(/[.\-/]/);
    return `${yyyy}-${mm.padStart(2, "0")}-${dd.padStart(2, "0")}`;
  }
  if (/^\d{4}-\d{2}-\d{2}$/.test(str)) return str;
  return "";
}
export default function TimeSelector({
  date, setDate,
  start, setStart,
  end, setEnd,
  endDate, setEndDate
}) {
  const safeDate = normalizeDate(date);
  const safeEndDate = normalizeDate(endDate);
  return (
    <form style={{ display: "flex", gap: 12, alignItems: "center" }} onSubmit={e => { e.preventDefault(); }}>
      <label>
        Start Date:
        <input
          type="date"
          value={safeDate}
          onChange={e => setDate(normalizeDate(e.target.value))}
        />
      </label>
      <label>
        Start Time:
        <input type="time" value={start} onChange={e => setStart(e.target.value)} />
      </label>
      <label>
        End Date:
        <input
          type="date"
          value={safeEndDate}
          onChange={e => setEndDate(normalizeDate(e.target.value))}
        />
      </label>
      <label>
        End Time:
        <input type="time" value={end} onChange={e => setEnd(e.target.value)} />
      </label>
    </form>
  );
}
