import React, { useEffect, useState } from "react";
import { Doughnut, Bar } from "react-chartjs-2";
import "./App.css";

export default function AnalyticsPage() {
  const [analytics, setAnalytics] = useState({
    slots: [],
    summary: { statusCounts: {} }
  });
  const [showMode, setShowMode] = useState("top10");

  useEffect(() => {
    fetch("http://localhost:4000/api/analytics")
      .then((res) => res.json())
      .then(setAnalytics);
  }, []);

  const { slots, summary } = analytics;

  // Calculate sensor status counts (for new doughnut chart)
  const sensorStatusCounts = slots.reduce(
    (acc, slot) => {
      if (slot.sensorStatus === "working") acc.working += 1;
      else if (slot.sensorStatus === "out_of_status") acc.outOfStatus += 1;
      return acc;
    },
    { working: 0, outOfStatus: 0 }
  );

  // Prepare sensor status doughnut data
  const sensorDoughnutData = {
    labels: ["Working Properly", "Out of Status"],
    datasets: [
      {
        data: [sensorStatusCounts.working, sensorStatusCounts.outOfStatus],
        backgroundColor: ["var(--success)", "var(--error)"],
        borderWidth: 2
      }
    ]
  };

  // Bar chart data (unchanged)
  const topNSlots = [...slots]
    .sort((a, b) => b.totalRevenue - a.totalRevenue)
    .slice(0, showMode === "top10" ? 10 : slots.length);

  const barChartData = {
    labels: topNSlots.map((slot) => `Slot ${slot.id}`),
    datasets: [
      {
        label: "Bookings",
        data: topNSlots.map((slot) => slot.totalBookings),
        backgroundColor: "#1976d2",
        borderRadius: 6,
        maxBarThickness: 28
      },
      {
        label: "Revenue (€)",
        data: topNSlots.map((slot) => slot.totalRevenue),
        backgroundColor: "#ff9800",
        borderRadius: 6,
        maxBarThickness: 28
      }
    ]
  };

  const chartOptions = {
    plugins: {
      legend: { position: "bottom" }
    },
    responsive: true,
    maintainAspectRatio: false,
    barPercentage: 0.9,
    categoryPercentage: 0.9,
    scales: {
      x: {
        stacked: true,
        grid: { display: false },
        ticks: { autoSkip: true, maxRotation: 45, minRotation: 45 }
      },
      y: {
        beginAtZero: true,
        grid: { color: "#ccc" }
      }
    }
  };

  return (
    <div className="analytics-page">
      <div className="analytics-summary">
        <div className="summary-cards">
          <div className="summary-card">
            <span>Total Slots</span>
            <strong>{slots.length}</strong>
          </div>
          {/* Add more summary cards as needed */}
        </div>
      </div>
      <div className="charts-section">
        {/* --- Updated Sensor Status Doughnut Chart (left) --- */}
        <div className="chart-card">
          <h3>Slot Sensor Status</h3>
          <Doughnut
            data={sensorDoughnutData}
            options={{
              plugins: { legend: { position: "bottom" } }
            }}
            height={280}
          />
        </div>
        {/* --- Top Bookings/Revenue Bar Chart (right) --- */}
        <div className="chart-card">
          <h3>
            Top Slots
            <span className="chart-mode-tabs">
              <button
                className={showMode === "top10" ? "active" : ""}
                onClick={() => setShowMode("top10")}
              >
                Top 10
              </button>
              <button
                className={showMode === "all" ? "active" : ""}
                onClick={() => setShowMode("all")}
              >
                All
              </button>
            </span>
          </h3>
          <div className="bar-chart-container" style={{ height: 340 }}>
            <Bar data={barChartData} options={chartOptions} />
          </div>
        </div>
      </div>
      {/* Slot leaderboard table (unchanged) */}
      <div className="leaderboard-section">
        <h3>Slot Leaderboard</h3>
        <table className="analytics-table">
          <thead>
            <tr>
              <th>Rank</th>
              <th>Slot ID</th>
              <th>Status</th>
              <th>Bookings</th>
              <th>Revenue (€)</th>
            </tr>
          </thead>
          <tbody>
            {topNSlots.map((slot, i) => (
              <tr key={slot.id}>
                <td>{i + 1}</td>
                <td>{slot.id}</td>
                <td>{(slot.status || "").replaceAll("_", " ")}</td>
                <td>{slot.totalBookings}</td>
                <td>€{slot.totalRevenue}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
