import React, { useEffect, useState, useMemo } from 'react';
import { io } from 'socket.io-client';
import SlotGrid from './SlotGrid';
import AnalyticsPage from './AnalyticsPage';
import TimeSelector from './TimeSelector';
import AuthPage from './AuthPage';
import './App.css';
import { Chart as ChartJS, ArcElement, BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

function App() {
  // --- USER AUTH ---
  const [currentUser, setCurrentUser] = useState(localStorage.getItem('currentUser'));
  const handleAuthSuccess = () => setCurrentUser(localStorage.getItem('currentUser'));
  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    setCurrentUser(null);
  };

  // --- DASHBOARD STATE ---
  const [slots, setSlots] = useState([]);
  const [selectedSlots, setSelectedSlots] = useState([]);
  const [showAnalytics, setShowAnalytics] = useState(false);
  const [date, setDate] = useState('');
  const [start, setStart] = useState('');
  const [end, setEnd] = useState('');
  const [endDate, setEndDate] = useState('');
  const [availableSlotData, setAvailableSlotData] = useState([]);
  const [socket, setSocket] = useState(null);

  // --- WEBSOCKET & DATA EFFECTS ---
  useEffect(() => {
    const newSocket = io('http://localhost:4000');
    setSocket(newSocket);
    return () => newSocket.close();
  }, []);

  useEffect(() => {
    if (!date || !start || !end || !endDate) return;
    fetch('http://localhost:4000/api/available-slots', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ date, start, end, endDate })
    })
      .then((res) => res.json())
      .then((data) => setAvailableSlotData(data.slots));
  }, [date, start, end, endDate]);

  // --- PRICE CALCULATION ---
  const totalPrice = useMemo(() => {
    return selectedSlots.reduce((sum, slotId) => {
      const slot = availableSlotData.find((s) => s.id === slotId);
      return sum + (slot?.price || 0);
    }, 0);
  }, [selectedSlots, availableSlotData]);

  // --- EXAMPLE BOOKING HANDLER ---
  // (Replace with your actual reserve/cancel handlers as needed)
  const handleReserveSelected = () => {
    if (selectedSlots.length === 0) {
      alert('Please select at least one slot.');
      return;
    }
    if (!date || !start || !end || !endDate) {
      alert('Please select date and time.');
      return;
    }
    if (!socket) {
      alert('Connection error. Please refresh the page.');
      return;
    }
    socket.emit(
      'bookBulk',
      { slotIds: selectedSlots, date, start, end, endDate },
      (response) => {
        if (response.success) {
          alert('Reservation successful!');
          setSelectedSlots([]);
        } else {
          alert(response.msg || 'Reservation failed. Please try again.');
        }
      }
    );
  };

  // >>>>>>>>>>>>>>>> AUTH CHECK MUST BE HERE, AFTER ALL HOOKS <<<<<<<<<<<<<<<<
  if (!currentUser) {
    return <AuthPage onAuthSuccess={handleAuthSuccess} />;
  }

  // >>>>>>>>>>>>>>>> PASTE YOUR ENTIRE DASHBOARD UI HERE (START) <<<<<<<<<<<<<<<<
  return (
    <div className="App">
      <header>
        <h1>Parking Dashboard</h1>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </header>

      {showAnalytics ? (
        <>
          <button
            className="analytics-btn"
            onClick={() => setShowAnalytics(false)}
          >
            ← Back to Slots
          </button>
          <AnalyticsPage />
        </>
      ) : (
        <>
          <TimeSelector
            date={date}
            setDate={setDate}
            start={start}
            setStart={setStart}
            end={end}
            setEnd={setEnd}
            endDate={endDate}
            setEndDate={setEndDate}
          />
          <div className="slot-actions">
            <span className="price-sum">
              {selectedSlots.length ? `Total Price: €${totalPrice}` : 'Select slot(s)'}
            </span>
            <button id="reserve-btn" onClick={handleReserveSelected}>
              Reserve Selected ({selectedSlots.length})
            </button>
            <button
              id="analytics-btn"
              onClick={() => setShowAnalytics(true)}
            >
              Show Analytics
            </button>
          </div>
          <Status slots={slots} />
          <SlotGrid
            slots={slots}
            date={date}
            start={start}
            end={end}
            endDate={endDate}
            selectedSlots={selectedSlots}
            toggleSlotSelection={toggleSlotSelection}
            availableSlotData={availableSlotData}
          />
        </>
      )}
    </div>
  );
  // >>>>>>>>>>>>>>>> PASTE YOUR ENTIRE DASHBOARD UI HERE (END) <<<<<<<<<<<<<<<<
}

// >>>>>>>>>>>>>>>> Status COMPONENT (PASTE YOURS OR USE BELOW) <<<<<<<<<<<<<<<<
function Status({ slots }) {
  const available = slots.filter((s) => s.status === 'available').length;
  const reserved = slots.filter((s) => s.status === 'reserved').length;
  const outOfOrder = slots.filter((s) => s.status === 'out_of_order').length;
  return (
    <div className="status">
      <strong>Status:</strong> Available: {available} | Reserved: {reserved} |
      Out of Order: {outOfOrder}
    </div>
  );
}

// >>>>>>>>>>>>>>>> toggleSlotSelection HANDLER (PASTE YOURS OR USE BELOW) <<<<<<<<<<<<<<<<
function toggleSlotSelection(slotId, selectedSlots, setSelectedSlots) {
  setSelectedSlots(prev =>
    prev.includes(slotId)
      ? prev.filter(id => id !== slotId)
      : [...prev, slotId]
  );
}

export default App;
