import React, { useEffect, useState } from "react";
import './App.css';
import { normalizeDate } from './TimeSelector';
import AnalyticsPage from "./AnalyticsPage";


import { io } from "socket.io-client";

function SlotGrid({ slots, date, start, end, endDate, selectedSlots, toggleSlotSelection, availableSlotData }) {
  function isSlotBooked(slot, date, start, end, endDate) {
    if (slot.status === "reserved") return true;
    if (!slot.bookings) return false;
    const reqStart = new Date(`${date}T${start}:00`).getTime();
    const reqEnd = new Date(`${endDate}T${end}:00`).getTime();
    return slot.bookings.some(b => {
      const bStart = new Date(`${b.date}T${b.start}:00`).getTime();
      const bEnd = new Date(`${b.endDate}T${b.end}:00`).getTime();
      return !(reqEnd <= bStart || reqStart >= bEnd);
    });
  }

  if (!slots || slots.length === 0) {
    return <div className="slot-grid">No slots available</div>;
  }

  return (
    <div className="slot-grid">
      {slots.map(slot => {
        const booked = isSlotBooked(slot, date, start, end, endDate);
        const available = slot.status === "available" && !booked;
        const isSelected = selectedSlots.includes(slot.id);
        const slotData = availableSlotData.find(s => s.id === slot.id);
        let className = "slot";
        if (isSelected) className += " selected";
        if (booked || slot.status === "reserved") className += " reserved";
        if (available) className += " available";
        return (
          <div
            key={slot.id}
            className={className}
            onClick={() => toggleSlotSelection(slot.id)}
          >
            <p>Slot {slot.id}</p>
            {slotData && <p className="slot-price">€{slotData.price}</p>}
          </div>
        );
      })}
    </div>
  );
}

function TimeSelector({ date, setDate, start, setStart, end, setEnd, endDate, setEndDate }) {
  function normalizeDate(str) {
    if (!str) return "";
    if (/^\d{2}[.\-/]\d{2}[.\-/]\d{4}$/.test(str)) {
      const [dd, mm, yyyy] = str.split(/[.\-/]/);
      return `${yyyy}-${mm.padStart(2, "0")}-${dd.padStart(2, "0")}`;
    }
    if (/^\d{4}-\d{2}-\d{2}$/.test(str)) return str;
    return "";
  }
  const safeDate = normalizeDate(date);
  const safeEndDate = normalizeDate(endDate);
  return (
    <div className="time-selector">
      <label>Date</label>
      <input type="date" value={safeDate} onChange={(e) => setDate(e.target.value)} placeholder="Date" />
      <label>Start</label>
      <input type="time" value={start} onChange={(e) => setStart(e.target.value)} placeholder="Start time" />
      <label>End</label>
      <input type="time" value={end} onChange={(e) => setEnd(e.target.value)} placeholder="End time" />
      <label>End Date (if different)</label>
      <input type="date" value={safeEndDate} onChange={(e) => setEndDate(e.target.value)} placeholder="End Date" />
    </div>
  );
}

function AnalyticsModal({ onClose }) {
  const [analytics, setAnalytics] = useState({ slots: [] });
  useEffect(() => {
    fetch("http://localhost:4000/api/analytics")
      .then(res => res.json())
      .then(data => setAnalytics(data));
  }, []);
  return (
    <div className="analytics-modal">
      <h2>Slot Analytics</h2>
      <table className="analytics-table">
        <thead>
          <tr>
            <th>Slot ID</th>
            <th>Total Bookings</th>
            <th>Total Revenue (€)</th>
          </tr>
        </thead>
        <tbody>
          {analytics.slots.map(slot => (
            <tr key={slot.id}>
              <td>{slot.id}</td>
              <td>{slot.totalBookings}</td>
              <td>€{slot.totalRevenue}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={onClose} style={{ marginTop: "20px" }}>Close</button>
    </div>
  );
}

const socket = io("http://localhost:4000");

export default function App() {
  function todayStr() {
    const now = new Date();
    const yyyy = now.getFullYear();
    const mm = String(now.getMonth() + 1).padStart(2, "0");
    const dd = String(now.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  }
  function normalizeDate(str) {
    if (!str) return "";
    if (/^\d{2}[.\-/]\d{2}[.\-/]\d{4}$/.test(str)) {
      const [dd, mm, yyyy] = str.split(/[.\-/]/);
      return `${yyyy}-${mm.padStart(2, "0")}-${dd.padStart(2, "0")}`;
    }
    if (/^\d{4}-\d{2}-\d{2}$/.test(str)) return str;
    return todayStr();
  }

  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [slots, setSlots] = useState([]);
  const [availableSlotData, setAvailableSlotData] = useState([]);
  const [selectedSlots, setSelectedSlots] = useState([]);
  const [date, setDate] = useState(todayStr());
  const [start, setStart] = useState("09:00");
  const [end, setEnd] = useState("10:00");
  const [endDate, setEndDate] = useState(todayStr());
  const [status, setStatus] = useState("");
  const [showAnalytics, setShowAnalytics] = useState(false);

  useEffect(() => {
    const currentUser = localStorage.getItem("currentUser");
    setIsAuthenticated(!!currentUser);
  }, []);

  useEffect(() => {
    setDate(d => normalizeDate(d));
    setEndDate(d => normalizeDate(d));
  }, []);

  useEffect(() => {
    socket.on("slots", setSlots);
    return () => socket.off("slots", setSlots);
  }, []);

  useEffect(() => {
    fetch("http://localhost:4000/api/available-slots", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ date: normalizeDate(date), start, end, endDate: normalizeDate(endDate) }),
    })
      .then(res => res.json())
      .then(data => setAvailableSlotData(data.slots || []));
  }, [date, start, end, endDate, slots]);

  function AuthPage({ onAuthSuccess }) {
    const [mode, setMode] = useState("signin");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    function handleSignUp(e) {
      e.preventDefault();
      if (!email || !password) return setError("Enter all fields");
      const users = JSON.parse(localStorage.getItem("users") || "{}");
      if (users[email]) return setError("Email already registered");
      users[email] = password;
      localStorage.setItem("users", JSON.stringify(users));
      setMode("signin");
      setError("Sign up successful! Please sign in.");
      setEmail("");
      setPassword("");
    }

    function handleSignIn(e) {
      e.preventDefault();
      const users = JSON.parse(localStorage.getItem("users") || "{}");
      if (!users[email]) return setError("User not found");
      if (users[email] !== password) return setError("Wrong password");
      localStorage.setItem("currentUser", email);
      onAuthSuccess();
    }

    return (
      <div className="auth-card">
        <h2>{mode === "signin" ? "Sign In" : "Sign Up"}</h2>
        <form onSubmit={mode === "signin" ? handleSignIn : handleSignUp}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
          <button type="submit">{mode === "signin" ? "Sign In" : "Sign Up"}</button>
        </form>
        {error && <div className="status-message">{error}</div>}
        <button onClick={() => setMode((prev) => (prev === "signin" ? "signup" : "signin"))}>
          {mode === "signin" ? "Need an account? Sign Up" : "Have an account? Sign In"}
        </button>
      </div>
    );
  }

  function handleAuthSuccess() {
    setIsAuthenticated(true);
  }

  function handleLogout() {
    localStorage.removeItem("currentUser");
    setIsAuthenticated(false);
  }

  function toggleSlotSelection(slotId) {
    setSelectedSlots((prev) =>
      prev.includes(slotId) ? prev.filter((id) => id !== slotId) : [...prev, slotId]
    );
  }

  function reserveSelectedSlots() {
    if (selectedSlots.length === 0) return;
    socket.emit(
      "bookBulk",
      {
        slotIds: selectedSlots,
        date: normalizeDate(date),
        start,
        end,
        endDate: normalizeDate(endDate),
      },
      (res) => {
        if (res.success) {
          setStatus(`Successfully booked ${selectedSlots.length} slots.`);
          setSelectedSlots([]);
        } else {
          setStatus(`Booking failed: ${res.msg}`);
        }
      }
    );
  }

  const priceSum = availableSlotData
    .filter((s) => selectedSlots.includes(s.id))
    .reduce((sum, s) => sum + (s.price || 0), 0);

  if (!isAuthenticated) {
    return <AuthPage onAuthSuccess={handleAuthSuccess} />;
  }

  return (
    <div className="App">
      <header>
        <h1>Parking Slot Booking</h1>
        <button onClick={handleLogout} className="logout-btn">Log Out</button>
      </header>
      <TimeSelector
        date={date}
        setDate={setDate}
        start={start}
        setStart={setStart}
        end={end}
        setEnd={setEnd}
        endDate={endDate}
        setEndDate={setEndDate}
      />
      <SlotGrid
        slots={slots}
        date={normalizeDate(date)}
        start={start}
        end={end}
        endDate={normalizeDate(endDate)}
        selectedSlots={selectedSlots}
        toggleSlotSelection={toggleSlotSelection}
        availableSlotData={availableSlotData}
      />
      <div className="slot-actions">
        <button
          onClick={reserveSelectedSlots}
          disabled={selectedSlots.length === 0}
          id="reserve-btn"
        >
          Reserve Selected
        </button>
        <span className="price-sum">
          {priceSum > 0 && `Total: €${priceSum.toFixed(2)}`}
        </span>
        <button
          onClick={() => setShowAnalytics(true)}
          id="analytics-btn"
        >
          Analytics
        </button>
      </div>
      <div className="status">{status}</div>
      {showAnalytics && <AnalyticsPage />}


    </div>
  );
}
