import React from "react";

function isSlotBooked(slot, date, start, end, endDate) {
  if (slot.status === "reserved") return true;
  if (!slot.bookings) return false;
  const reqStart = new Date(`${date}T${start}:00`).getTime();
  const reqEnd = new Date(`${endDate}T${end}:00`).getTime();
  return slot.bookings.some(b => {
    const bStart = new Date(`${b.date}T${b.start}:00`).getTime();
    const bEnd = new Date(`${b.endDate}T${b.end}:00`).getTime();
    return !(reqEnd <= bStart || reqStart >= bEnd);
  });
}

export default function SlotGrid({
  slots,
  date,
  start,
  end,
  endDate,
  selectedSlots,
  toggleSlotSelection,
  availableSlotData
}) {
  if (!slots || slots.length === 0) {
    return <div>No parking slots available. (Check backend is running!)</div>;
  }
  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(5, 1fr)",
        gridGap: 12,
        margin: "20px 0"
      }}
    >
      {slots.map((s, idx) => {
        const isOutOfOrder = s.status === "out_of_order";
        const isBooked = isSlotBooked(s, date, start, end, endDate);
        const isSelected = selectedSlots.includes(s.id);
        const isDisabled = isOutOfOrder || isBooked;
        const slotPrice = availableSlotData?.find(d => d.id === s.id)?.price;
        return (
          <div
            key={s.id}
            onClick={() => !isDisabled && toggleSlotSelection(s.id)}
            style={{
              padding: 18,
              minHeight: 75,
              minWidth: 90,
              border: "2px solid",
              borderColor: isOutOfOrder
                ? "#999"
                : isBooked
                ? "#d42"
                : isSelected
                ? "#1885f2"
                : "#2c8",
              background: isOutOfOrder
                ? "#eee"
                : isSelected
                ? "#dff5ff"
                : isBooked
                ? "#ffe5e5"
                : "#e7ffe7",
              cursor: isDisabled ? "not-allowed" : "pointer",
              position: "relative",
              borderRadius: 7,
              boxSizing: "border-box",
              opacity: isOutOfOrder ? 0.6 : 1
            }}
          >
            <b>Slot {s.id}</b>
            <div style={{ color: isOutOfOrder ? "#999" : isBooked ? "#d42" : "#289" }}>
              {isOutOfOrder
                ? "Out of Order"
                : isBooked
                ? "Booked"
                : isSelected
                ? "Selected"
                : "Available"}
            </div>
            {!isBooked && !isOutOfOrder && slotPrice !== undefined && (
              <div style={{ color: "#16a200" }}>€{slotPrice}</div>
            )}
            {isSelected && !isBooked && !isOutOfOrder && (
              <div
                style={{
                  position: "absolute",
                  top: 6,
                  right: 6,
                  background: "#1885f2",
                  color: "white",
                  borderRadius: "50%",
                  width: 20,
                  height: 20,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  fontSize: 14,
                  fontWeight: "bold"
                }}
              >
                ✓
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}
