import React, { useState } from "react";

export default function AuthPage({ onAuthSuccess }) {
  const [mode, setMode] = useState("signin");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

    function handleSignUp(e) {
        e.preventDefault();
        if (!email || !password) return setError("Enter all fields");
        const users = JSON.parse(localStorage.getItem("users") || "{}");
        if (users[email]) return setError("Email already registered");
        users[email] = password;
        localStorage.setItem("users", JSON.stringify(users));
        setMode("signin");
        setError("Sign up successful! Please sign in.");
        setEmail("");
        setPassword("");
    }


    function handleSignIn(e) {
        e.preventDefault();
        const users = JSON.parse(localStorage.getItem("users") || "{}");
        if (!users[email]) return setError("User not found");
        if (users[email] !== password) return setError("Wrong password");
        localStorage.setItem("currentUser", email);
        onAuthSuccess();
    }


  return (
    <div style={{ maxWidth: "400px", margin: "2rem auto", padding: "2rem", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
      <h2>{mode === "signin" ? "Sign In" : "Sign Up"}</h2>
      <form onSubmit={mode === "signin" ? handleSignIn : handleSignUp}>
        <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" required style={{ width: "100%", margin: "0.5rem 0", padding: "0.5rem" }} />
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required style={{ width: "100%", margin: "0.5rem 0", padding: "0.5rem" }} />
        <button type="submit" style={{ width: "100%", margin: "1rem 0", padding: "0.75rem", background: "#1976d2", color: "white", border: "none", borderRadius: "4px" }}>
          {mode === "signin" ? "Sign In" : "Sign Up"}
        </button>
      </form>
      <div style={{color: "red", margin: "0.5rem 0"}}>{error}</div>
      <button 
        onClick={() => setMode(prev => prev === "signin" ? "signup" : "signin")} 
        style={{ background: "none", border: "none", color: "#1976d2", cursor: "pointer", textDecoration: "underline"}}
      >
        {mode === "signin" ? "Need an account? Sign Up" : "Have an account? Sign In"}
      </button>
    </div>
  );
}
