import React, { useEffect, useState, useMemo } from 'react';
import { io } from 'socket.io-client';
import SlotGrid from './SlotGrid';
import AnalyticsPage from './AnalyticsPage';
import TimeSelector from './TimeSelector';
import './App.css';

function App() {
  const [slots, setSlots] = useState([]);
  const [selectedSlots, setSelectedSlots] = useState([]);
  const [showAnalytics, setShowAnalytics] = useState(false);
  const [date, setDate] = useState('');
  const [start, setStart] = useState('');
  const [end, setEnd] = useState('');
  const [endDate, setEndDate] = useState('');
  const [availableSlotData, setAvailableSlotData] = useState([]);
  const [socket, setSocket] = useState(null);

  // Initialize WebSocket connection
  useEffect(() => {
    const newSocket = io('http://localhost:4000');
    setSocket(newSocket);

    newSocket.on('slots', (data) => {
      setSlots(data);
    });

    return () => newSocket.close();
  }, []);

  // Fetch available slots when date/time changes
  useEffect(() => {
    if (!date || !start || !end || !endDate) return;

    fetch('http://localhost:4000/api/available-slots', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ date, start, end, endDate })
    })
      .then(res => res.json())
      .then(data => setAvailableSlotData(data.slots))
      .catch(err => console.error('Error fetching available slots:', err));
  }, [date, start, end, endDate]);

  const toggleSlotSelection = (slotId) => {
    setSelectedSlots(prev =>
      prev.includes(slotId)
        ? prev.filter(id => id !== slotId)
        : [...prev, slotId]
    );
  };

  // Calculate total price for selected slots (frontend-only, for UI)
  const totalPrice = useMemo(() => {
    return selectedSlots.reduce((sum, slotId) => {
      const slot = availableSlotData.find(s => s.id === slotId);
      return sum + (slot?.price || 0);
    }, 0);
  }, [selectedSlots, availableSlotData]);

  // Handle reserving selected slots via WebSocket
  const handleReserveSelected = () => {
    if (!selectedSlots.length) {
      alert('Please select at least one slot.');
      return;
    }
    if (!date || !start || !end || !endDate) {
      alert('Please select date and time.');
      return;
    }
    if (!socket) {
      alert('Connection error. Please refresh the page.');
      return;
    }

    socket.emit(
      'bookBulk',
      { slotIds: selectedSlots, date, start, end, endDate },
      (response) => {
        if (response.success) {
          alert('Reservation successful!');
          setSelectedSlots([]);
        } else {
          alert(response.msg || 'Reservation failed. Please try again.');
        }
      }
    );
  };

  return (
    <div className="App">
      <header>
        <h1>Parking Slot Reservation</h1>
        <div>
          <button
            id="analytics-btn"
            onClick={() => setShowAnalytics(!showAnalytics)}
          >
            {showAnalytics ? 'Hide Analytics' : 'Show Analytics'}
          </button>
        </div>
      </header>

      {showAnalytics ? (
        <AnalyticsPage />
      ) : (
        <>
          <TimeSelector
            date={date}
            setDate={setDate}
            start={start}
            setStart={setStart}
            end={setEnd}
            setEnd={setEndDate}
            endDate={endDate}
          />

          <SlotGrid
            slots={slots}
            date={date}
            start={start}
            end={end}
            endDate={endDate}
            selectedSlots={selectedSlots}
            toggleSlotSelection={toggleSlotSelection}
            availableSlotData={availableSlotData}
          />

          {selectedSlots.length > 0 && (
            <div className="slot-actions">
              <span className="price-sum">Total: â‚¬{totalPrice}</span>
              <button
                id="reserve-btn"
                onClick={handleReserveSelected}
                disabled={!selectedSlots.length}
              >
                Reserve Selected
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
